import { swapiRouter } from './swapi.router';
import * as Hapi from '@hapi/hapi';

const init = async () => {
    const server = Hapi.server({
        port: 3000,
        host: 'localhost',
    });
    server.route(swapiRouter.getRoutes());
    await server.start();
    console.log('Server running on %s', server.info.uri);
};

process.on('unhandledRejection', (err) => {
    console.log(err);
    process.exit(1);
});

init();

import { swapiHandler } from './swapi/swapi.handler';
import { ServerRoute } from '@hapi/hapi';

export const swapiRouter = {
    getRoutes(): ServerRoute[] {
        return [
            {
                path: '/',
                method: 'get',
                handler: (req, res) => swapiHandler.handle(req, res),
            },
        ];
    },
};

import { swapiUtil } from './swapi.util';
import { swapiService } from './swapi.service';

export const swapiHandler = {
    async handle(req, res) {
        try {
            const input = swapiUtil.validateRequest(req);
            if (!input) {
                return res.response(`Bad request - The parameters 'type' and 'name' are both required.`).code(400);
            }
            const data = await swapiService.lookupItem(input.type, input.num);
            const transformedData = swapiUtil.transformResponse(data);
            return transformedData;
        } catch (error) {
            return res.response(`Internal Server Error: ${error.message}`).code(500);
        }
    },
};

import axios from 'axios';

export const swapiService = {
    async lookupItem(type: string, num: string) {
        const root = 'https://swapi.dev/api';
        const url = `${root}/${type}/${num}`;
        const result = await axios.get(url);
        return result.data;
    },
};

export const swapiUtil = {
    validateRequest(req): ValidateRequestOutput {
        const { type, num } = req.query;
        if (!type || !num) {
            return false;
        } else {
            return { type, num };
        }
    },
    transformResponse(data) {
        return {
            nameAndSize: `${data.name}-${data.mass || data.length || 'unknown'}`,
            hasStarships: !!data.starships && data.starships.length > 0,
        };
    },
};

export type ValidateRequestOutput = false | ValidateRequestOutputSuccess;

export interface ValidateRequestOutputSuccess {
    type: string;
    num: string;
}
